            $(""#ExtLink"").change(function () {
                var video_url = $(this).val();
                if (video_url) {
                    var youtube_id = video_url.match(/^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#\&\?]*).*/).pop();
